    componentDidMount() {
        this.props.requestApiData();
    }

    person = (user) => (
        <div key={user.id}>
            <h1>{user.firstName}</h1> 
        </div>
    );       

    render() {
        const { results = {} } = this.props.data; 
        console.log("results:   "+results.length);
