//                 <li key={item.id}>
//                 User ID:    {item.id}
//                 </li>
//             ))}
//         </ul>
//     </div>
//   ); 
const mapStateToProps = state => ({ data: state.data });

const mapDispatchToProps = dispatch => bindActionCreators({ requestApiData }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(Home);
